mapper.select:
SELECT
FROM t_employee e JOIN t_employee_detail ed ON ed.id = e.id
-- 부서명 LEFT JOIN t_commoncode_item ci2 ON e.department_code = ci2.minor_code AND ci2.major_code = 'DEPARTMENT_CODE'
-- 하위부서명 LEFT JOIN t_department_info di ON e.department_code = di.department_code AND e.sub_department_code = di.child_code
-- 직책명 LEFT JOIN t_commoncode_item ci3 ON e.position_code = ci3.minor_code AND ci3.major_code = 'POSITION_CODE'
AND e.department_code = #{departmentCode}
AND e.sub_department_code = #{subDepartmentCode}
AND e.position_code = #{positionCode}
AND ( e.id LIKE CONCAT('%', #{searchKeyword}, '%') OR e.name LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY e.id LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

SELECT COUNT(*) FROM t_employee e JOIN t_employee_detail ed ON ed.id = e.id
-- 부서명 LEFT JOIN t_commoncode_item ci2 ON e.department_code = ci2.minor_code AND ci2.major_code = 'DEPARTMENT_CODE'
-- 하위부서명 LEFT JOIN t_department_info di ON e.department_code = di.department_code AND e.sub_department_code = di.child_code
-- 직책명 LEFT JOIN t_commoncode_item ci3 ON e.position_code = ci3.minor_code AND ci3.major_code = 'POSITION_CODE'
AND e.department_code = #{departmentCode}
AND e.sub_department_code = #{subDepartmentCode}
AND e.position_code = #{positionCode}
AND ( e.id LIKE CONCAT('%', #{searchKeyword}, '%') OR e.name LIKE CONCAT('%', #{searchKeyword}, '%') )

SELECT child_code AS code, child_name AS name
  FROM t_department_info
 WHERE parent_code = #{departmentCode}


SELECT COUNT(*)
  FROM workerBase


		AND (
			id LIKE CONCAT('%', #{searchKeyword}, '%')
			OR name LIKE CONCAT('%', #{searchKeyword}, '%')
		)


	AND is_supervisor = #{isSupervisor}


	AND is_lines_manager = #{isLinesManager}

,pattern AS ( select idx, id AS employee_id, name AS employee_name, department_name, sub_department_name, position_name, is_supervisor, is_lines_manager, is_forklift_operator, is_driver from workerBase
AND ( id LIKE CONCAT('%', #{searchKeyword}, '%') OR name LIKE CONCAT('%', #{searchKeyword}, '%') )
AND is_supervisor = #{isSupervisor}
AND is_lines_manager = #{isLinesManager}
