mapper.insert:
INSERT INTO t_approval (
approval_no, approval_type, doc_no, title, content, progress_status, drafter_id, draft_date, due_date, approver1, approver2, approver3,
) VALUES (
#{approvalNo}, #{approvalType}, #{docNo}, #{title}, #{content}, 1, -- 전자결재 초기값 1(결재요청) #{drafterId}, #{draftDate}, #{dueDate}, #{approver1}, #{approver2}, #{approver3},
)

mapper.update:
UPDATE t_approval
approval_type = #{approvalType}, doc_no = #{docNo}, title = #{title}, content = #{content}, draft_date = #{draftDate}, due_date = #{dueDate}, approver1 = #{approver1}, approver2 = #{approver2}, approver3 = #{approver3}, mod_id = #{modId}, mod_date = now(),
WHERE approval_no = #{approvalNo}
