mapper.select:

		SELECT 

			 , ci1.minor_name AS progressName
			 , ci2.minor_name AS approvalTypeName
		  FROM empBase e


		  JOIN t_approval a
		    ON e.id = a.drafter_id

	      LEFT JOIN t_approval_items ai
   			ON ai.doc_no = a.doc_no

		  JOIN t_commoncode_item ci1
		    ON a.progress_status = ci1.minor_code
		   AND ci1.major_code = 'PROGRESS_STATUS'

		  JOIN t_commoncode_item ci2
		    ON a.approval_type = ci2.minor_code
		   AND ci2.major_code = 'APPROVAL_TYPE'

			1 = 1

			   AND (a.drafter_id = #{drafterId}	
			    OR  ai.approver1 = #{drafterId}	
			    OR (ai.approver2 = #{drafterId} AND a.progress_status != 1 AND ai.approver1_status = '3') 
			    OR (ai.approver3 = #{drafterId} AND a.progress_status != 1 AND ai.approver2_status = '3') 
			     )


			   AND e.name LIKE CONCAT('%', #{drafterName}, '%')


			   AND a.title LIKE CONCAT('%', #{title}, '%')


			   AND ci1.minor_code = #{progressStatus}


			   AND ci2.minor_code = #{approvalType}


			   AND DATE(a.draft_date) >= #{requestStartDate}


			   AND DATE(a.draft_date) <= #{requestEndDate}


			   AND DATE(a.due_date) >= #{dueStartDate}


			   AND DATE(a.due_date) <= #{dueEndDate}


		 ORDER BY a.draft_date DESC
		 LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}


		SELECT COUNT(*)
		  FROM empBase e


		  JOIN t_approval a
		    ON e.id = a.drafter_id

	      LEFT JOIN t_approval_items ai
   			ON ai.doc_no = a.doc_no

		  JOIN t_commoncode_item ci1
		    ON a.progress_status = ci1.minor_code
		   AND ci1.major_code = 'PROGRESS_STATUS'

		  JOIN t_commoncode_item ci2
		    ON a.approval_type = ci2.minor_code
		   AND ci2.major_code = 'APPROVAL_TYPE'

			1 = 1

			   AND (a.drafter_id = #{drafterId}
			    OR  ai.approver1 = #{drafterId}
			    OR (ai.approver2 = #{drafterId} AND a.progress_status != 1 AND ai.approver1_status = '3')
			    OR (ai.approver3 = #{drafterId} AND a.progress_status != 1 AND ai.approver2_status = '3')
			     )


			   AND e.name LIKE CONCAT('%', #{drafterName}, '%')


			   AND a.title LIKE CONCAT('%', #{title}, '%')


			   AND ci1.minor_code = #{progressStatus}


			   AND ci2.minor_code = #{approvalType}


			   AND DATE(a.draft_date) >= #{requestStartDate}


			   AND DATE(a.draft_date) <= #{requestEndDate}


			   AND DATE(a.due_date) >= #{dueStartDate}


			   AND DATE(a.due_date) <= #{dueEndDate}

mapper.update:
UPDATE t_approval
   SET
   	  progress_status = #{progressStatus}
   	, mod_id = #{modId}
   	, mod_date = now()
 WHERE doc_no = #{docNo}

UPDATE t_approval
approval_type = #{approvalType}, doc_no = #{docNo}, title = #{title}, content = #{content}, draft_date = #{draftDate}, due_date = #{dueDate}, approver1 = #{approver1}, approver2 = #{approver2}, approver3 = #{approver3}, mod_id = #{modId}, mod_date = now(),
WHERE idx = #{idx}

mapper.select:
SELECT a.idx , a.title , ci1.minor_name AS approvalTypeName , ci2.minor_name AS progressName , e.name , a.draft_date , a.due_date FROM t_approval a JOIN t_approval_items ai ON ai.doc_no = a.doc_no JOIN t_employee e ON a.drafter_id = e.id JOIN t_commoncode_item ci1 ON a.approval_type = ci1.minor_code AND ci1.major_code = 'APPROVAL_TYPE' JOIN t_commoncode_item ci2 ON a.progress_status = ci2.minor_code AND ci2.major_code = 'PROGRESS_STATUS'
AND (a.drafter_id = #{memberId} OR ai.approver1 = #{memberId} OR (ai.approver2 = #{memberId} AND a.progress_status != 1 AND ai.approver1_status = 'Y') OR (ai.approver3 = #{memberId} AND a.progress_status != 1 AND ai.approver2_status = 'Y') )
AND e.name LIKE CONCAT('%', #{search.drafterName}, '%')
AND a.title LIKE CONCAT('%', #{search.title}, '%')
AND ci1.minor_code = #{search.approvalType}
AND ci2.minor_code = #{search.progressStatus}
AND DATE(a.draft_date) >= #{search.requestStartDate}
AND DATE(a.draft_date) <= #{search.requestEndDate}
AND DATE(a.due_date) >= #{search.dueStartDate}
AND DATE(a.due_date) <= #{search.dueEndDate}
