mapper.select:
	    SELECT count(*) 
	    FROM t_product
	    WHERE is_deleted = 'N'

	        AND type = #{type}


	        AND name LIKE CONCAT('%', #{name}, '%')


	        AND unit = #{unit}


    		AND price >= #{minPrice}


		    AND price <= #{maxPrice}

SELECT 
    p.*
FROM t_product p
WHERE p.is_deleted = 'N'


    AND p.type = #{type}


    AND p.name LIKE CONCAT('%', #{name}, '%')


    AND p.unit = #{unit}


    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}

ORDER BY p.idx ASC
LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

SELECT * FROM t_product
WHERE idx = #{idx} 
AND is_deleted = 'N'
