mapper.select:
SELECT count(*)
FROM t_product
WHERE is_deleted = 'N'

	AND type = #{type}


	AND name LIKE CONCAT('%', #{name}, '%')


	AND quantity >= #{minQuantity}


	AND quantity <= #{maxQuantity}


	AND price >= #{minPrice}


	AND price <= #{maxPrice}


ORDER BY idx ASC

SELECT
p.*,
c.minor_name AS type_name
FROM t_product p
LEFT JOIN t_commoncode_item c
ON p.type = c.minor_code
AND c.major_code = 'PRODUCT_TYPE'
WHERE p.is_deleted = 'N'

	AND p.type = #{type}


	AND p.name LIKE CONCAT('%', #{name}, '%')


	AND p.quantity >= #{minQuantity}


	AND p.quantity <= #{maxQuantity}


	AND p.price >= #{minPrice}


	AND p.price <= #{maxPrice}


ORDER BY p.idx ASC
LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}
