mapper.select:
SELECT q.* FROM t_qc_standard q
AND q.name LIKE CONCAT('%', #{name}, '%')
AND q.memo LIKE CONCAT('%', #{memo}, '%')
AND q.is_deleted = #{isDeleted}
ORDER BY q.idx ASC LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

mapper.update:
UPDATE t_qc_standard
type = #{type}, name = #{name}, unit = #{unit}, target_value = #{targetValue}, min_value = #{minValue}, max_value = #{maxValue}, memo = #{memo}, is_deleted = #{isDeleted}, mod_id = #{modId}, mod_date = now(),
WHERE idx = #{idx}
