mapper.select:
SELECT COUNT(*)
FROM t_product
WHERE is_deleted = 'N'

	AND type = #{type}


	AND name LIKE CONCAT('%', #{name}, '%')


	AND quantity >= #{minQuantity}


	AND quantity <= #{maxQuantity}


	AND price >= #{minPrice}


	AND price <= #{maxPrice}

SELECT
r.*,
c.minor_name AS type_name,
cl.company_name AS client_company_name
FROM t_raw_material r
LEFT JOIN t_commoncode_item c 
ON p.type = c1.minor_code AND c1.major_code
LEFT JOIN t_bom b ON p.bom_idx = b.idx
LEFT JOIN t_client cl ON p.client_idx = cl.idx
WHERE p.is_deleted = 'N'

	AND p.type = #{type}


	AND p.name LIKE CONCAT('%', #{name}, '%')


	AND p.quantity >= #{minQuantity}


	AND p.quantity <= #{maxQuantity}


	AND p.price >= #{minPrice}


	AND p.price <= #{maxPrice}

ORDER BY p.idx ASC
LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}
