mapper.select:
SELECT COUNT(*)
FROM t_raw_material
WHERE is_deleted = 'N'


	AND type = #{type}



	AND name LIKE CONCAT('%', #{name}, '%')



	AND client_idx = #{clientIdx}



	AND bom_idx = #{bomIdx}



	AND quantity >= #{minQuantity}



	AND quantity <= #{maxQuantity}



	AND price >= #{minPrice}



	AND price <= #{maxPrice}

SELECT
r.*,
c.company_name AS clientCompanyName,
b.name AS bomName
FROM
t_raw_material r
LEFT JOIN t_client c ON r.client_idx = c.idx
LEFT JOIN
t_bom b ON r.bom_idx = b.idx
WHERE r.is_deleted = 'N'


	AND r.type = #{type}



	AND r.name LIKE CONCAT('%', #{name}, '%')



	AND r.client_idx = #{clientIdx}



	AND r.bom_idx = #{bomIdx}



	AND r.quantity >= #{minQuantity}



	AND r.quantity <= #{maxQuantity}



	AND r.price >= #{minPrice}



	AND r.price <= #{maxPrice}


ORDER BY r.idx ASC
LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

SELECT idx, name
FROM t_raw_material
WHERE idx = #{idx}
