mapper.select:
SELECT COUNT(*) FROM t_warehouse_transaction w
AND w.trans_type LIKE CONCAT('%', #{transType}, '%')
AND w.product_idx = #{productIdx}
AND w.material_idx = #{materialIdx}
AND w.is_deleted = #{isDeleted}
AND w.manufacturing_date BETWEEN STR_TO_DATE(CONCAT(#{startManufacturingDate}, '000000'), '%Y-%m-%d%H%i%S') AND STR_TO_DATE(CONCAT(#{endManufacturingDate}, '235959'), '%Y-%m-%d%H%i%S')
AND w.manufacturing_date >= STR_TO_DATE(CONCAT(#{startManufacturingDate}, '000000'), '%Y-%m-%d%H%i%S')
AND w.manufacturing_date <= STR_TO_DATE(CONCAT(#{endManufacturingDate}, '235959'), '%Y-%m-%d%H%i%S')
AND w.quantity >= #{minQuantity}
AND w.quantity <= #{maxQuantity}

SELECT w.*, p.name AS productName, m.name AS materialName, sl.name AS source_location_name, dl.name AS destination_location_name, c.minor_name AS trans_type_name, ci.minor_name AS unit_name FROM t_warehouse_transaction w LEFT JOIN t_product p ON w.product_idx = p.idx LEFT JOIN t_raw_material m ON w.material_idx = m.idx LEFT JOIN t_location_info sl ON w.source_location = sl.idx LEFT JOIN t_location_info dl ON w.destination_location = dl.idx LEFT JOIN t_commoncode_item c ON w.trans_type = c.minor_code AND c.major_code = "TRANS_TYPE" LEFT JOIN t_commoncode_item ci ON w.unit = ci.minor_code AND ci.major_code = "ITEM_UNIT"
AND w.trans_type LIKE CONCAT('%', #{transType}, '%')
AND w.product_idx = #{productIdx}
AND w.material_idx = #{materialIdx}
AND w.is_deleted = #{isDeleted}
AND w.manufacturing_date BETWEEN STR_TO_DATE(CONCAT(#{startManufacturingDate}, '000000'), '%Y-%m-%d%H%i%S') AND STR_TO_DATE(CONCAT(#{endManufacturingDate}, '235959'), '%Y-%m-%d%H%i%S')
AND w.manufacturing_date >= STR_TO_DATE(CONCAT(#{startManufacturingDate}, '000000'), '%Y-%m-%d%H%i%S')
AND w.manufacturing_date <= STR_TO_DATE(CONCAT(#{endManufacturingDate}, '235959'), '%Y-%m-%d%H%i%S')
AND w.quantity >= #{minQuantity}
AND w.quantity <= #{maxQuantity}
ORDER BY w.idx ASC LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

mapper.update:
UPDATE t_warehouse_transaction
trans_type = #{transType}, code = #{code}, product_idx = #{productIdx}, material_idx = #{materialIdx}, unit = #{unit}, quantity = #{quantity}, status = #{status}, manufacturing_date = #{manufacturingDate}, source_location = #{destinationLocation}, destination_location = #{destinationLocation}, memo = #{memo}, is_deleted = #{isDeleted}, mod_id = #{modId}, mod_date = now(),
WHERE idx = #{idx}

mapper.select:
WITH material AS ( SELECT rm.idx, rm.type, ci1.minor_name AS type_name, rm.qc_type, rm.name, rm.expiry_date FROM t_raw_material rm JOIN ( SELECT minor_code, minor_name FROM t_commoncode_item WHERE major_code = 'MATERIAL_TYPE' ) AS ci1 ON ci1.minor_code = rm.type WHERE rm.is_deleted = 'N' AND rm.parents_idx is null ), product AS ( SELECT p.idx, p.name, p.code, p.spec, p.expiry_date, p.status, ci1.minor_name AS statusName FROM t_product p JOIN ( SELECT minor_code, minor_name FROM t_commoncode_item WHERE major_code = 'PRODUCT_STATUS' ) AS ci1 ON ci1.minor_code = p.status WHERE is_deleted = 'N' ), pattern AS ( SELECT wt.idx, wt.trans_type, ci1.minor_name AS trans_type_name, wt.code, wt.product_idx, p.name AS product_name, wt.material_idx, m.name as material_name, replace(m.qc_type,'|','-') as qc_type, wt.unit, ci3.minor_name as unit_name, wt.quantity, wt.status, ci2.minor_name as status_name FROM t_warehouse_transaction wt JOIN ( SELECT minor_code, minor_name FROM t_commoncode_item WHERE major_code = 'TRANS_TYPE' ) AS ci1 ON ci1.minor_code = wt.trans_type JOIN ( SELECT minor_code, minor_name FROM t_commoncode_item WHERE major_code = 'PROCESS_FLOW_STATUS' ) AS ci2 ON ci2.minor_code = wt.status JOIN ( SELECT minor_code, minor_name FROM t_commoncode_item WHERE major_code = 'ITEM_UNIT' ) AS ci3 ON ci3.minor_code = wt.unit LEFT JOIN material AS m ON m.idx = wt.material_idx LEFT JOIN product AS p ON p.idx = wt.product_idx
AND wt.status = #{status}
AND m.name LIKE CONCAT('%', #{materialName}, '%')
