mapper.select:
SELECT count(*) FROM t_order o LEFT JOIN t_order_detail d ON o.document_number = d.document_number
o.trade_code = #{tradeCode}
and o.document_number = #{documentNumber}
and o.status_code = #{statusCode}
and o.company_name = #{companyName}
and o.request_date BETWEEN STR_TO_DATE(CONCAT(#{requestStartDate},'000000'),'%Y-%m-%d%H%i%S') AND STR_TO_DATE(CONCAT(#{requestEndDate}, '235959'),'%Y-%m-%d%H%i%S')
and o.request_date >= STR_TO_DATE(CONCAT(#{requestStartDate},'000000'),'%Y-%m-%d%H%i%S')
and o.request_date <= STR_TO_DATE(CONCAT(#{requestEndDate}, '235959'),'%Y-%m-%d%H%i%S')
and o.is_deleted = #{isDeleted}
and o.due_date BETWEEN STR_TO_DATE(CONCAT(#{dueStartDate},'000000'),'%Y-%m-%d%H%i%S') AND STR_TO_DATE(CONCAT(#{dueEndDate}, '235959'),'%Y-%m-%d%H%i%S')
and o.due_date >= STR_TO_DATE(CONCAT(#{dueStartDate},'000000'),'%Y-%m-%d%H%i%S')
and o.due_date <= STR_TO_DATE(CONCAT(#{dueEndDate}, '235959'),'%Y-%m-%d%H%i%S')
ORDER BY o.reg_date DESC

SELECT 
	
  FROM t_order o
  LEFT JOIN t_order_detail d
	ON o.document_number = d.document_number

	o.trade_code = #{tradeCode}
	
		and o.document_number = #{documentNumber}
	
	
		and o.status_code = #{statusCode}
	
	
		and o.company_name = #{companyName}
	
	
		
			and o.request_date BETWEEN STR_TO_DATE(CONCAT(#{requestStartDate},'000000'),'%Y-%m-%d%H%i%S') 
									AND STR_TO_DATE(CONCAT(#{requestEndDate}, '235959'),'%Y-%m-%d%H%i%S')
		
		
			and o.request_date >= STR_TO_DATE(CONCAT(#{requestStartDate},'000000'),'%Y-%m-%d%H%i%S')
		
		
			and o.request_date <= STR_TO_DATE(CONCAT(#{requestEndDate}, '235959'),'%Y-%m-%d%H%i%S')
		
	
	
		and o.is_deleted = #{isDeleted}
	
	
		
			and o.due_date BETWEEN STR_TO_DATE(CONCAT(#{dueStartDate},'000000'),'%Y-%m-%d%H%i%S') 
								AND STR_TO_DATE(CONCAT(#{dueEndDate}, '235959'),'%Y-%m-%d%H%i%S')
		
		
			and o.due_date >= STR_TO_DATE(CONCAT(#{dueStartDate},'000000'),'%Y-%m-%d%H%i%S')
		
		
			and o.due_date <= STR_TO_DATE(CONCAT(#{dueEndDate}, '235959'),'%Y-%m-%d%H%i%S')
		
	

ORDER BY o.reg_date DESC
LIMIT #{pageDTO.size} OFFSET #{pageDTO.offset}

mapper.update:
UPDATE t_order
status_code = #{statusCode} -- 주문상태_코드 , company_name = #{companyName} -- 거래처명 , company_number = #{companyNumber} -- 사업자번호 , request_date = #{requestDate} -- 요청일자 , expected_date = #{expectedDate} -- 예정일 , due_date = #{dueDate} -- 마감일자 , destination_name = #{destinationName} -- 목적지명 , receiver = #{receiver} -- 받는사람 , phone1 = #{phone1} -- 전화번호1 , phone2 = #{phone2} -- 전화번호2 , post_code = #{postCode} -- 우편번호 , address1 = #{address1} -- 목적지주소1 , address2 = #{address2} -- 목적지주소2 , special_issue = #{specialIssue} -- 특이사항 , is_deleted = #{isDeleted} -- 삭제유무 , mod_id = #{modId} -- 최종 작성자 , mod_date = now() -- 최종작성일자
WHERE document_number = #{documentNumber}

UPDATE t_order_detail
department_code = #{departmentCode} -- 담당부서_코드 , sub_department_code = #{subDepartmentCode} -- 담당하위부서_코드 , client_name = #{clientName} -- 담당자_이름 , client_phone = #{clientPhone} -- 담당자_연락처 , mod_id = #{modId} -- 최종 작성자 , mod_date = now() -- 최종작성일자
WHERE document_number = #{documentNumber}
