mapper.update:
UPDATE T_CLIENT
BUSINESS_NUMBER = #{businessNumber}, COMPANY_NAME = #{companyName}, OWNER_NAME = #{ownerName}, COMPANY_CATEGORY = #{companyCategory}, COMPANY_SUBJECT = #{companySubject}, PHONE_NUMBER = #{phoneNumber}, E_MAIL = #{eMail}, FAX_NUMBER = #{faxNumber}, ADDRESS1 = #{address1}, ADDRESS2 = #{address2}, CREDIT_LIMIT = #{creditLimit}, IS_DELETED = #{isDeleted}, MEMO = #{memo}, STATUS = #{status}, STATUS_MESSAGE = #{statusMessage}, MOD_ID = NULL, -- 임시로 NULL MOD_DATE = NOW(),
WHERE BUSINESS_NUMBER = #{businessNumber}

mapper.select:
SELECT COUNT(BUSINESS_NUMBER)
  FROM T_CLIENT_INFO
 WHERE BUSINESS_NUMBER = #{businessNumber}

mapper.insert:
INSERT INTO T_CLIENT_INFO (
BUSINESS_NUMBER, CLIENT_EMPLOYEE_NAME, CLIENT_EMPLOYEE_MEMO, PHONE1, PHONE2, REG_ID, REG_DATE,
) VALUES (
#{businessNumber}, #{clientEmployeeName}, #{clientEmployeeMemo}, #{phone1}, #{phone2}, NULL, -- 최초작성자 임시로 NULL NOW(),
)

mapper.update:
UPDATE T_CLIENT_INFO
CLIENT_EMPLOYEE_NAME = #{clientEmployeeName}, CLIENT_EMPLOYEE_MEMO = #{clientEmployeeMemo}, PHONE1 = #{phone1}, PHONE2 = #{phone2}, MOD_ID = NULL, -- 최종수정자 임시로 NULL MOD_DATE = NOW(),
WHERE BUSINESS_NUMBER = #{businessNumber}
